    private void jLabelProjectsAddMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jLabelProjectsAddMouseClicked
        
        //Ao clicar é criado o objeto da tela de dialogo do projeto
        ProjectDialogScreen projectDialogScreen = new ProjectDialogScreen(this, rootPaneCheckingEnabled);
        //Habilita a visibilidade da tela de dialogo do projeto
        projectDialogScreen.setVisible(true);
  
    }//GEN-LAST:event_jLabelProjectsAddMouseClicked

    private void jLabelTasksAddMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jLabelTasksAddMouseClicked
        
        //Ao clicar é criado o objeto da tela de dialogo de tarefa
        TaskDialogScreen taskDialogScreen = new TaskDialogScreen(this, rootPaneCheckingEnabled);
        //comentar aqui
        taskDialogScreen.setProject(null);
        //Habilita a visibilidade da tela de dialogo de tarefa
        taskDialogScreen.setVisible(true);

    }//GEN-LAST:event_jLabelTasksAddMouseClicked

